#include <iostream>
#include<vector>
using namespace std;

class Solution {
public:
    int threeSumMulti(vector<int>& A, int target) {
        const long mod=1000000007;
        long ans=0;
        sort(A.begin(),A.end());
        for(int i=0;i<A.size();i++){
            int t=target-A[i];
            int j=i+1;
            int k=(int)A.size()-1;
            while(j<k){
                if(A[j]+A[k]<t){
                    j++;
                }else if(A[j]+A[k]>t){
                    k--;
                }else if(A[j]!=A[k]){
                    int left=1;
                    int right=1;
                    while(j+1<k&&A[j]==A[j+1]){
                        left++;
                        j++;
                    }
                    while(k+1>j&&A[k]==A[k-1]){
                        right++;
                        k--;
                    }
                    ans=ans+left*right;
                    ans %= mod;
                    j++;
                    k--;
                }else{
                    //一共有k-j+1个数
                    //c(n,2)组合数
                    ans+=(k-j+1)*(k-j)/2;
                    ans%=mod;
                    break;
                }
               
            }
        }
        return static_cast<int>(ans);
    }
};

